CREATE TABLE institution (id INT AUTO_INCREMENT NOT NULL, name VARCHAR(255) NOT NULL, address VARCHAR(1000) NOT NULL, city VARCHAR(255) NOT NULL, postal_code VARCHAR(255) DEFAULT NULL, country VARCHAR(255) NOT NULL, established_year INT DEFAULT NULL, type VARCHAR(255) NOT NULL, website VARCHAR(255) DEFAULT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB;
CREATE TABLE assignment (id INT AUTO_INCREMENT NOT NULL, course_id INT NOT NULL, title VARCHAR(255) NOT NULL, description LONGTEXT NOT NULL, INDEX IDX_assignment_course_id (course_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB;
CREATE TABLE course (id INT AUTO_INCREMENT NOT NULL, department_id INT NOT NULL, instructor_id INT NOT NULL, title VARCHAR(255) NOT NULL, code VARCHAR(255) NOT NULL, description LONGTEXT NOT NULL, INDEX IDX_course_department_id (department_id), INDEX IDX_course_instructor_id (instructor_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB;
CREATE TABLE student (id INT AUTO_INCREMENT NOT NULL, department_id INT NOT NULL, full_name VARCHAR(255) NOT NULL, email VARCHAR(255) NOT NULL, date_of_birth DATE NOT NULL, gender VARCHAR(255) DEFAULT NULL, enrolment_year INT NOT NULL, status VARCHAR(255) NOT NULL, INDEX IDX_student_department_id (department_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB;
CREATE TABLE enrolment (id INT AUTO_INCREMENT NOT NULL, student_id INT NOT NULL, course_id INT NOT NULL, INDEX IDX_enrolment_student_id (student_id), INDEX IDX_enrolment_course_id (course_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB;
CREATE TABLE department (id INT AUTO_INCREMENT NOT NULL, institution_id INT NOT NULL, name VARCHAR(255) NOT NULL, head_of_department VARCHAR(255) NOT NULL, building VARCHAR(255) NOT NULL, contact_email VARCHAR(255) NOT NULL, INDEX IDX_department_institution_id (institution_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB;
CREATE TABLE instructor (id INT AUTO_INCREMENT NOT NULL, department_id INT NOT NULL, full_name VARCHAR(255) NOT NULL, email VARCHAR(255) NOT NULL, INDEX IDX_instructor_department_id (department_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB;
ALTER TABLE assignment ADD CONSTRAINT FK_assignment_course_id FOREIGN KEY (course_id) REFERENCES course (id);
ALTER TABLE course ADD CONSTRAINT FK_course_department_id FOREIGN KEY (department_id) REFERENCES department (id);
ALTER TABLE course ADD CONSTRAINT FK_course_instructor_id FOREIGN KEY (instructor_id) REFERENCES instructor (id);
ALTER TABLE student ADD CONSTRAINT FK_student_department_id FOREIGN KEY (department_id) REFERENCES department (id);
ALTER TABLE enrolment ADD CONSTRAINT FK_enrolment_student_id FOREIGN KEY (student_id) REFERENCES student (id);
ALTER TABLE enrolment ADD CONSTRAINT FK_enrolment_course_id FOREIGN KEY (course_id) REFERENCES course (id);
ALTER TABLE department ADD CONSTRAINT FK_department_institution_id FOREIGN KEY (institution_id) REFERENCES institution (id);
ALTER TABLE instructor ADD CONSTRAINT FK_instructor_department_id FOREIGN KEY (department_id) REFERENCES department (id);
